/*Main style*/
/*Colors*/
$background-color: #f7f7f7;
$nav-background: rgb(28, 35, 60);
$icon-color: #636A7F;
$icon-hover-background: #172A45;
$icon-hover-color: #F1F5FC;
$icon-border-left-color: #56C3E6;
$slider-card-background: #ffffff;
$gender-f-background: #f5e0f7;
$gender-f-color: #ac7ab8;
$button-color: #1a233c;

* {
    padding: 0;
    margin: 0;
    box-sizing: border-box;
    // border: 1px solid red;
    font-family: 'Inter', sans-serif;
}

html,
body {
    background-color: $background-color;
}

/*MAIN WRAPPER*/
.main-wrapper {
    // position: relative;
    background-color: $background-color;
    align-items: center;
}

/*MODAL*/
.modal {
    position: fixed;
    background: rgba(28, 35, 60, 0.3);
    z-index: 3;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    display: grid;
    align-items: center;
    justify-items: center;
    pointer-events: none;
    opacity: 0;
    transition: opacity 0.5s;

    .inner-modal {
        position: relative;
        background-color: $background-color;
        font-size: 1.5em;
        padding: 25px;
        display: grid;
        border-radius: 20px;
        box-shadow: 0px 15px 30px -8px rgba(23, 42, 69, 1);

        .modal-close {
            position: absolute;
            top: 0;
            right: 0;
            font-size: 20px;
            font-weight: bold;
            color: $background-color;
            background-color: $nav-background;
            padding: 7px;
            border-radius: 5px;
            transform: translate(10px, -10px);
            box-shadow: 0px 1px 3px 2px rgba(28, 35, 60, 0.19);
            cursor: pointer;
        }

        section {
            padding: 20px 0;
            display: grid;

            .modal-mother-info,
            .modal-father-info {
                padding: 10px 20px;
            }

            i {
                font-size: 25px;
                color: $nav-background;
                padding: 5px;
                margin-right: 20px;
            }

            .fa-phone {
                font-size: 20px;
            }
        }

    }
}

.modal-open {
    pointer-events: all;
    opacity: 1;
}

/*MODAL END*/

/*WRAPPER*/
.wrapper {
    width: 100%;
    display: grid;
    grid-template-columns: 15% 85%;
    // background-color: blue;
}

/*NAVIGATION BAR*/
.navigation-wrapper {
    height: 100vh;
    display: grid;
    justify-content: center;
    align-items: center;
    padding: 0;

    .navigation-bar {
        background-color: $nav-background;
        display: grid;
        justify-content: center;
        border-radius: 25px;

        ul {
            padding: 10px 0;



            li {
                list-style: none;
                display: grid;
                justify-content: center;
                margin: 1rem 0;
                padding: 1rem;
                transition: all 0.5s;

                i {
                    color: $icon-color;
                    font-size: 25px;
                    color: $icon-color;
                    transition: all 0.5s;
                }
            }

            li:hover {
                box-shadow: inset 3px 0px 0px 0px $icon-border-left-color;
                background-color: $icon-hover-background;
                cursor: pointer;

                i {
                    color: $icon-hover-color;
                    cursor: pointer;
                }
            }

            .logo {
                img {
                    object-fit: cover;
                    width: 50px;
                    height: 50px;

                    border-radius: 50%;
                    border: 2px solid $icon-border-left-color;
                }

            }
        }
    }
}

/*NAVIGATION BAR END*/

/*SIDE WRAPPER*/
.side-wrapper {
    // display: grid;
    justify-content: center;
}

/*SIDE WRAPPER END*/

/*HEADER*/
.header {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    justify-content: center;
    text-align: center;
    padding: 3rem;

    .management-items {
        display: flex;
        padding: 20px;

        .wl-add-item {
            background-color: $button-color;
            color: $icon-hover-color;
            padding: 7px 15px;
            border-radius: 20px;
            font-weight: bold;
            letter-spacing: 2px;
            border: none;
            transition: all 0.2s ease-out;
            cursor: pointer;
            // box-shadow: 0px 2px 5px 3px rgba(28, 35, 60, 0.19);

            i {
                margin-right: 10px;
            }
        }

        .wl-add-item:hover {
            transform: translateY(-3px);
        }
    }
}

/*HEADER*/

/*SLIDER*/
.slider {
    display: grid;
    justify-content: center;
    grid-template-columns: repeat(2, 300px);
    padding: 2em;

    .sliderCard {
        background-color: $slider-card-background;
        margin: 20px;
        padding: 20px;
        text-align: center;
        border-radius: 15px;
        box-shadow: 0px 5px 10px 3px rgba(28, 35, 60, 0.19);
        cursor: pointer;
        transition: all 0.5s;

        i {
            font-size: 20px;
            padding: 5px;
            border-radius: 50%;
        }

        h5 {
            font-weight: 300;
            font-size: 15px;
            padding: 5px 0;
        }

        span {
            font-size: 30px;
        }
    }

    .sliderCard:hover {
        transform: translateY(-10px);
    }
}

/*SLIDER END*/

/*TABLE*/
.group-list {
    display: grid;
    justify-content: center;
    grid-template-columns: 70%;

    .table-style {
        display: grid;
        font-size: 20px;
        padding: 10px;

        .table-row {
            display: flex;
            flex-wrap: nowrap;
            margin: 10px;
            transition: all 0.2s ease-in;
            justify-content: space-between;

            .table-cell-wrapper {
                display: flex;
            }

            .table-cell {
                padding: 10px 10px;
                // text-align: right;
                // margin: 0 10px;

                i {
                    font-size: 15px;
                    border-radius: 50%;
                }

                .age-icon {
                    color: black;
                    font-size: 20px;
                    padding: 0 10px;
                    border-radius: 0%;
                }
            }

            // .age-cell {
            //     text-align: left;
            // }

        }

        .table-row:hover {
            background-color: #ffffff;
            border-radius: 15px;
            cursor: pointer;
            box-shadow: 0px 2px 5px 3px rgba(28, 35, 60, 0.19);
            transform: translateY(-5px);
        }
    }
}

/*TABLE END*/

/*FORM*/
.as-form {
    display: grid;
    padding: 50px;

    ::placeholder {
        color: $icon-color;
    }

    .tab {
        display: none;
        margin: 20px;
    }

    .input-control {
        position: relative;
        display: grid;
        padding-bottom: 20px;
    }

    .separator {
        height: 2px;
        width: 100%;
        background-color: $button-color;
        display: block;
        transform: scaleX(0) translateY(-2px);
        transform-origin: 50%;
        opacity: 0;
        transition: all .3s linear;
    }

    input {
        background-color: transparent;
        margin: 0px 0;
        padding: 10px;
        font-size: 13px;
        border: none;
        outline: none;
        border-bottom: 1px solid $icon-color;

        &:focus+.separator {
            transform: scaleX(1) translateY(-2px);
            opacity: 1;
        }
    }


    input[type='submit'],
    .btn {
        background-color: $button-color;
        color: $icon-hover-color;
        padding: 7px 15px;
        border-radius: 20px;
        font-weight: bold;
        letter-spacing: 2px;
        border: none;
        transition: all 0.2s ease-out;
        cursor: pointer;
    }



    .as-form-select {
        select {
            color: $icon-color;
            border: none;
            border-bottom: 1px solid $icon-color;
            padding: 10px 5px;
            box-shadow: none;
            background: transparent;
            background-image: none;
        }

        option {
            background-color: transparent;
            outline: none;
            padding: 10px;
        }

        select:focus {
            outline: none;
        }
    }

    .error-msg {
        position: absolute;
        bottom: 0;
        left: 0;
        text-align: center;
        font-size: 15px;
        opacity: 0;
        transition: all 0.5s;
    }

    .input-err-msg {
        opacity: 1;
    }




}

/*FORM END*/
